definition.script:
node {
    stage('Build') {
        checkout([
            $class: 'GitSCM',
            branches: [[name: '*/main']],
            doGenerateSubmoduleConfigurations: false,
            extensions: [],
            userRemoteConfigs: [[url: 'REDACTED://github.com/suki-aiagent/poc-java-app1.git']]
        ])
        sh '''
            cd app
            gradle build -x test
        '''
    }

    stage('Test') {
        try {
            sh '''
                cd app
                gradle test
            '''
        } finally {
            // Publish JUnit reports even if tests fail
            junit 'app/build/test-results/test/*.xml'
        }
    }
}
